		Convert(datetime,convert(varchar(10),DATEADD(s,-1,DATEADD(mm, 
		DATEDIFF(m,0,convert(datetime,'01/'+@MesA+'/'+@anio1,103))+1,0)),103),103) AS fecha_documento, 
		(Case When @tMovimiento='FACTUR' Then 
