		--glosa='COBRANZA COMERCIAL DEL '+upper(convert(varchar(12),fechai,113))+' EN '+@RazonSocial,
		--ccosto='', fefec='',
		--Banco=@RazonSocial
		--from (select fechai,left(ltrim(cap),2) codcap,cap,sum(total) total,sum(dif) dif
		--		from exportaventas
		--		group by fechai,left(ltrim(cap),2),cap
