							ELSE 0 END)
					  ELSE 0 END),

	naturalezahaber	=(CASE WHEN p.TipoCuenta IN ('N','R','U') THEN 
						   (CASE WHEN (CASE WHEN (sum(tmp.inhaber)+sum(tmp.anhaber)+sum(tmp.haber))>(sum(tmp.indebe)+sum(tmp.andebe)+sum(tmp.debe)) 
